set names utf8;
Drop database if exists xiuse;
create database xiuse charset=utf8;
use xiuse;
#大厅
create table hall(
    id int primary key auto_increment,
    hallBg_src varchar(100),
    hallRanking_src varchar(100),
    RankIcon_src varchar(100),
    sign varchar(50),
    watchIcon_src varchar(100),
    watchNum bigint
);
