/* 周星榜 二级菜单*/
.header{
    line-height: 0px;
}
.header .rankChoice{
    display: flex;
    justify-content: center;
    align-items:center;
    margin-bottom:15px;
}
.header .rankChoice .starRank{
    color:#fff;
}
.header .rankChoice  .dropDown{
    width:14px;
    height:14px;
    margin-left:4px;
    position: relative;
    background-image:url("//127.0.0.1:7000/img/rank_down_arrow.png");
    background-size: 100%;
    background-repeat: no-repeat;
    background-position: center;
}
.header .rankChoice .dropMenu{
    position:absolute;
    top:14px;
    left:-74px;
    background-image: url("//127.0.0.1:7000/img/pop_rank_bg.png");
    padding: 15px 5px; 
    background-repeat: no-repeat;
    background-position: center;
    background-size: 100% 100%;   
    /* display:none; */
    z-index: 10;
}
.header .rankChoice .dropMenu a{
    display: block;
    color:gray;
    text-align: center;
    padding:2px 25px;
    font-size: 14px;
    white-space: nowrap;
}
.header .rankChoice .dropMenu a.active{
     background:#FF6261;
}
  /*模拟的标题*/
  .header{
    z-index: 9990;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 70px;
    line-height: 16px;
    text-align: center;
    /* background-color: white; */
    background-image: url("//127.0.0.1:7000/img/anchor_rank_tab_bg.png");
    background-repeat: no-repeat;
    background-size: 100%;
    background-position: center;
    color:#fff;
  }
  .header .btn-left{
    position: absolute;
    top: 0;
    left: 0;
    padding:12px 12px 0 12px;
  }
  .header .title{
    margin-top: 12px;
  }
  /*菜单*/
  .tabs-warp{
    height: 42px;/*高度比tabs-content小, 目的是隐藏tabs的水平滚动条*/
    overflow-y: hidden;
    box-sizing: content-box;
  }
  .tabs-warp .tabs-content{
    width: 100%;
    height: 50px;
    overflow-x: auto;
  }
  .tabs-warp .tabs-content .tabs{
    width: 100%;
    white-space: nowrap;
    background: rgba(250, 250, 250, 0.3);
    border-radius: 20px;
  }
  .tabs-warp .tabs-content .tabs li{
    display: inline-block;
    height: 31px;
    line-height: 34px;
    vertical-align: middle;
  }
  /* .tabs-warp .tabs-content .tabs .active{
    color: #FF6990;
  } */
  /*菜单进度*/
  .tabs-warp .tab-bar{
    position: absolute;
    height: 31px;
    background-color: #fff;
    transition: left 300ms;
    top:0;
    left:0;
    color:#FF6990;
    line-height: 34px;
    border-radius: 20px;
  }
  /*列表*/
  .swiper-container{
    position: fixed;
    top: 69px;
    left: 0;
    right: 0;
    bottom: 0;
    background-color:#fff;
  }

  /*展示上拉加载的数据列表*/
  .data-li{
    position: relative;
    height: 80px;
    padding: 10px 8px 10px 120px;
    border-bottom: 1px solid #eee;
  }
  .data-li .pd-img{
    position: absolute;
    left: 18px;
    top: 10px;
    width: 80px;
    height: 80px;
  }
  .data-li .pd-name{
    font-size: 13px;
    line-height: 20px;
    height: 40px;
    margin-bottom: 10px;
    overflow: hidden;
  }
  .data-li .pd-price{
    font-size: 13px;
    color: red;
  }
  .data-li .pd-sold{
    font-size: 12px;
    margin-top: 8px;
    color: gray;
  }
  /* 主播列表 前三名*/
 .clearMarTop:before{
    width:100%;
    content:"";
    display:table;
}
.section .wallBox{
    background-image: url("//127.0.0.1:7000/img/anchor_rank_top_sub_rank1.png");
    height:150px;
    background-repeat: no-repeat;
    background-size:100%;
    background-position:center -38px;
    background-color: #fff;
}
.section .wall{
    display:flex;
    justify-content: center;
    padding-top:20px;
}
.section .wall .link{
    display:block;
    position: relative;
    padding:0 20px;
}
.section .wall .picFra{
    width:76px;
    height:84px;
    position: absolute;
    top:-11px;
    z-index: 999;
    left:15px;
    background-repeat: no-repeat;
    background-position: center;
    background-size: 100%;
}
.section .wall li:first-child .picFra{
    background-image: url("//127.0.0.1:7000/img/anchor_rank_top2.png");
}
.section .wall li:nth-child(2) .picFra{
    background-image: url("//127.0.0.1:7000/img/anchor_rank_top1.png");
}
.section .wall li:last-child .picFra{
    background-image: url("//127.0.0.1:7000/img/anchor_rank_top3.png");
}
.section .wall  .picFraCir{
    overflow:hidden;
    width:66px;
    height:66px;
    border-radius: 50%;
}
.section .wall .picFraCir img{
    width:100%;
    height:auto;
}
.section .wall .sign{
    position: absolute;
    width:100px;
    height:20px;
    background-size: 100%;
    background-position: center;
    background-repeat: no-repeat;
    color:#fff;
    text-align: center;
    bottom:-11px;
    left:50%;
    transform:translatex(-50%);
    line-height: 20px;
}
.wall li:first-child .sign{
    background-image: url("//127.0.0.1:7000/img/anchor_rank_top2_name.png");
}
.wall li:nth-child(2) .sign{
    background-image: url("//127.0.0.1:7000/img/anchor_rank_top1_name.png");
}
.wall li:last-child .sign{
    background-image: url("//127.0.0.1:7000/img/anchor_rank_top3_name.png");
}
.wall .sign span{
   text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
}
/* 对冠军头像处理 */
.section .wall li:nth-child(2) .picFraCir{
    width:74px;
    height:74px;
    transform: translateY(-8px);
}
.wall li:nth-child(2) .picFra{
    width:86px;
    height:88px; 
    left:14px;
    top:-17px;
}
.section .wall .score{
    position: absolute;
    top:38px;
    right:22px;
    z-index: 999;
    height:15px;
    width:28px;
    display: flex;
    
}
.section .wall .score img{
    width:auto;
    height:100%;
}
