}
.section .AnchorList .listBox .lisPicBox{
    width:12%;
}
.section .AnchorList .listBox .lisPic{
    width:50px;
    height:50px;
    overflow: hidden;
    border-radius: 50%;
    border:1px solid #FF8F9B;
}
.section .AnchorList .listBox .lisPic img{
    width:100%;
